mapper.select:
select id,trace_id as traceId,trace_content as traceContent,trace_dt as traceDt 
from biz_warning 
where id = #{bizWarning.id};

mapper.update:
update biz_warning set 

	trace_id = #{traceId}


	trace_content = #{traceContent}


	trace_dt = #{traceDt}

where id = #{id};

mapper.select:
select id,trace_id as traceId,trace_content as traceContent,trace_dt as traceDt from biz_Warning where 1=1

	and traceId = #{traceId}


	and traceContent = #{traceContent}

ORDER BY id asc

select id,trace_id as traceId,trace_content as traceContent,trace_dt as traceDt 
from biz_Warning 
where 1=1 and trace_dt between #{bizStartDate} and #{bizEndDate}
