mapper.update:
update statistics_day set
	total_time_num = #{totalTimeNum},
	start_time = #{startTime}	
where 1=1;

mapper.select:
select start_time as startTime,total_time_num as totalTimeNum
from statistics_day 
where 1=1 start_time = #{startTime}

select start_time as startTime,total_time_num as totalTimeNum,timestamp
from statistics_day 
where 1=1 and timestamp > #{dayTradingStartDate} and timestamp < #{dayTradingEndDate}
